    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 20000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.protocol = null;
    this._listeners = {};
    this._reconnectTimer = null;
    this._connectTimeout = null;
    this._shouldReconnect = true;
    this._isConnecting = false;

    if (this.automaticOpen) {
      this.open();
    }
  }

  addEventListener(type, listener) {
    if (!this._listeners[type]) {
      this._listeners[type] = [];
    }
    this._listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this._listeners[type]) {
      this._listeners[type] = this._listeners[type].filter(l => l !== listener);
    }
  }

  dispatchEvent(event) {
    if (this[`on${event.type}`]) {
      this[`on${event.type}`](event);
    }
    if (this._listeners[event.type]) {
      this._listeners[event.type].forEach(listener => listener(event));
    }
  }

  createEvent(eventName, detail) {
    return new CustomEvent(eventName, { detail });
  }

  open(wasReconnect = false) {
    if (this.readyState === WebSocket.OPEN || this._isConnecting) {
      return;
    }

    this._isConnecting = true;
    this.readyState = WebSocket.CONNECTING;
    this.dispatchEvent(this.createEvent('connecting'));

    const connectAttempt = () => {
      try {
        this._ws = new WebSocket(this.url, this.protocols);
        this._setupSocketHandlers(wasReconnect);

        this._connectTimeout = setTimeout(() => {
          if (this.readyState === WebSocket.CONNECTING) {
            this._handleError(new Error('Connection timeout'));
            this._ws.close();
          }
        }, this.timeoutInterval);
      } catch (err) {
        this._handleError(err);
        this._scheduleReconnect();
      }
    };

    connectAttempt();
  }

  _setupSocketHandlers(wasReconnect) {
    this._ws.onopen = (event) => {
      clearTimeout(this._connectTimeout);
      this._isConnecting = false;
      this.reconnectAttempts = 0;
      this.readyState = WebSocket.OPEN;
      this.protocol = event.currentTarget.protocol;
      this.dispatchEvent(this.createEvent('open', { wasReconnect }));
    };

    this._ws.onclose = (event) => {
      clearTimeout(this._connectTimeout);
      this._isConnecting = false;
      this.readyState = WebSocket.CLOSED;

      if (this._shouldReconnect) {
        this._scheduleReconnect();
