      this.dispatchEvent(this.createEvent('close', { code: event.code, reason: event.reason, wasClean: event.wasClean }));
    };

    this._ws.onmessage = (event) => {
      this.dispatchEvent(this.createEvent('message', { data: event.data }));
    };

    this._ws.onerror = (error) => {
      this._handleError(error);
    };
