function manageTestExecution() {
    const socket = new WebSocket('ws://testserver.example.com/ws');
    const maxParallelRuns = 5;
    const subscriptionKey = Math.floor(Math.random() * 1000000);
    let queue = [
        { id: 'test1', status: 'queued' },
        { id: 'test2', status: 'queued' },
        { id: 'test3', status: 'queued' },
        { id: 'test4', status: 'queued' },
        { id: 'test5', status: 'queued' },
        { id: 'test6', status: 'queued' },
        { id: 'test7', status: 'queued' }
    ];
    let numRunning = 0;

    function updateDOM(testId, status, result = '') {
        let el = document.getElementById(`status-${testId}`);
        if (!el) {
            el = document.createElement('div');
            el.id = `status-${testId}`;
            document.body.appendChild(el);
        }
        el.textContent = `${testId}: ${status} ${result}`;
    }

    function processQueue() {
        while (numRunning < maxParallelRuns && queue.length > 0) {
            const test = queue.shift();
            test.status = 'running';
            numRunning++;

            // Simulate opening a test window and sending execution request
            const testWindow = window.open('', `_blank`);
            testWindow.document.write(`<h1>Running ${test.id}</h1>`);

            // Notify server about test start
            socket.send(JSON.stringify({
                type: 'test_start',
                key: subscriptionKey,
                testId: test.id
            }));

            // Simulate completion after timeout
            setTimeout(() => {
                const success = Math.random() > 0.3; // 70% success rate
                const result = success ? 'passed' : 'failed';

                // Notify server about test completion
                socket.send(JSON.stringify({
                    type: 'test_complete',
                    key: subscriptionKey,
                    testId: test.id,
                    result: result
                }));

                testWindow.close();
                test.status = 'completed';
                test.result = result;
                numRunning--;
                processQueue();
            }, 2000 + Math.random() * 3000);

            updateDOM(test.id, test.status);
        }
    }

    socket.onopen = () => {
        console.log('WebSocket connection established');

        // Send subscription message to server
        socket.send(JSON.stringify({
            type: 'subscribe',
            key: subscriptionKey
        }));

        // Start processing the queue once connected
        processQueue();
    };

    socket.onmessage = (event) => {
        try {
            const message = JSON.parse(event.data);

            if (message.type === 'test_update') {
                updateDOM(message.testId, message.status, message.result || '');
            } else if (message.type === 'queue_status') {
                console.log(`Tests queued: ${message.count}, Running: ${numRunning}`);
            } else if (message.type === 'session_init') {
                console.log(`Session initialized with key: ${message.sessionKey}`);
            }
        } catch (error) {
            console.error('Error processing message:', error);
        }
    };

    socket.onclose = () => {
        console.log('WebSocket connection closed');
    };

    socket.onerror = (error) => {
        console.error('WebSocket Error:', error);
    };
}

