class WebSocketClient {
  constructor(updateUrl, gridKey) {
    this.websocket = null;
    this.wxErrorCount = 0;
    this.updateUrl = updateUrl;
    this.gridKey = gridKey;
    this.retryTimer = null;
  }

  openWebSocket() {
    const url = `${this.updateUrl}?gridKey=${this.gridKey}`;
    this.websocket = new WebSocket(url);

    this.websocket.onopen = () => {
      this.wxErrorCount = 0;
      this.startHeartbeat();
    };

    this.websocket.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data);
        this.handleMessage(message);
      } catch (error) {
        console.error('Error parsing message:', error);
      }
    };

    this.websocket.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.websocket.close();
    };

    this.websocket.onclose = (event) => {
      this.stopHeartbeat();
      if (this.wxErrorCount < 5) {
        setTimeout(() => {
          this.wxErrorCount++;
          this.openWebSocket();
        }, 5000 * this.wxErrorCount);
      }
    };
  }

  sendMessage(data) {
    if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
      try {
        const compressedData = pako.gzip(encodeURIComponent(JSON.stringify(data)));
        this.websocket.send(compressedData);
      } catch (error) {
        console.error('Error sending message:', error);
      }
    } else {
      console.warn('WebSocket not open. Message not sent.');
    }
  }

  closeConnection() {
    if (this.websocket) {
      this.websocket.close();
    }
  }

  startHeartbeat() {
    this.stopHeartbeat();
    this.retryTimer = setInterval(() => {
      if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
        this.sendMessage({ type: 'heartbeat' });
      }
    }, 30000);
  }

  stopHeartbeat() {
    if (this.retryTimer) {
      clearInterval(this.retryTimer);
      this.retryTimer = null;
    }
  }

  handleMessage(message) {
    switch (message.type) {
      case 'update':
        this.handleUpdate(message.data);
        break;
      case 'error':
        this.handleError(message.error);
        break;
      case 'heartbeat':
        console.log('Heartbeat received');
        break;
      default:
        console.log('Unknown message type:', message.type);
    }
  }

  handleUpdate(data) {
    console.log('Received update:', data);
  }

  handleError(error) {
    console.error('Server error:', error);
  }
}

