class WS extends Transport {
  constructor(opts) {
    super(opts);
    this.supportsBinary = true;
    this.perMessageDeflate = opts.perMessageDeflate || {};
    this.ws = null;
    this.writable = false;
  }

  doOpen() {
    const uri = this.uri();
    const WebSocketCtor = typeof WebSocket !== 'undefined' ? WebSocket : require('ws');

    const wsOptions = {
      agent: this.opts.agent,
      pfx: this.opts.pfx,
      key: this.opts.key,
      passphrase: this.opts.passphrase,
      cert: this.opts.cert,
      ca: this.opts.ca,
      ciphers: this.opts.ciphers,
      rejectUnauthorized: this.opts.rejectUnauthorized,
      headers: this.opts.extraHeaders || {}
    };

    this.ws = new WebSocketCtor(uri, [], wsOptions);
    this.addEventListeners();
  }

  addEventListeners() {
    this.ws.onopen = () => {
      this.writable = true;
      this.emit('open');
    };

    this.ws.onclose = (event) => {
      this.onClose(event.reason, event.code);
    };

    this.ws.onmessage = (event) => {
      this.emit('data', event.data);
    };

    this.ws.onerror = (error) => {
      this.emit('error', error);
    };
  }

  write(packets) {
    packets.forEach(packet => {
      const encodedPacket = this.encodePacket(packet);
      if (this.writable && this.ws.readyState === WebSocket.OPEN) {
        this.ws.send(encodedPacket);
      } else {
        this.once('open', () => this.ws.send(encodedPacket));
      }
    });
  }

  onClose(reason, code) {
    this.writable = false;
    this.ws = null;
    super.onClose(reason, code);
  }

  doClose() {
    if (this.ws) {
      this.ws.close();
    }
  }

  uri() {
    let query = this.opts.query || {};
    if (this.opts.timestampRequests) {
      query[this.opts.timestampParam] = Date.now();
    }
    const queryString = Object.entries(query)
      .map(([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v)}`).join('&');
    const protocol = this.opts.secure ? 'wss' : 'ws';
    const port = this.opts.port ? `:${this.opts.port}` : '';
    return `${protocol}://${this.opts.hostname}${port}${this.opts.path}?${queryString}`;
  }

  static check() {
    return typeof WebSocket !== 'undefined' || typeof require !== 'undefined';
  }
}

