class RPCWebSocket {
    constructor(address, encryption, namespace) {
        this._namespace = namespace || '';
        this._listeners = {};
        this._handles = {};
        this.setAddress(address, encryption);
        this._connect();
    }

    setAddress(address, encryption) {
        const protocol = encryption ? 'wss' : 'ws';
        this._address = `${protocol}://${address}`;
    }

    addListener(method, callback) {
        if (!this._listeners[method]) {
            this._listeners[method] = [];
        }
        this._listeners[method].push(callback);
    }

    removeListener(method) {
        delete this._listeners[method];
    }

    request(method, params, id, successCallback, errorCallback) {
        const fullMethod = this._namespace ? `${this._namespace}.${method}` : method;
        const data = this._formatData(fullMethod, params, id);
        this._handles[id] = { success: successCallback, error: errorCallback };
        this._send(data);
    }

    batchRequest(requests, successCallback, errorCallback) {
        const batchId = Date.now();
        const batchData = requests.map(req => {
            const fullMethod = this._namespace ? `${this._namespace}.${req.method}` : req.method;
            return this._formatData(fullMethod, req.params, `${batchId}_${requests.indexOf(req)}`);
        });
        batchData.forEach((data, index) => {
            this._handles[data.id] = { success: successCallback, error: errorCallback };
        });
        this._send(batchData);
    }

    _formatData(method, params, id) {
        return {
            jsonrpc: '2.0',
            method,
            params,
            id
        };
    }

    _send(data) {
        if (this._socket && this._socket.readyState === WebSocket.OPEN) {
            this._socket.send(JSON.stringify(data));
        } else if (this._socket && this._socket.readyState === WebSocket.CONNECTING) {
            const checkAndSend = () => {
                if (this._socket.readyState === WebSocket.OPEN) {
                    this._socket.send(JSON.stringify(data));
                    clearInterval(interval);
                }
            };
            const interval = setInterval(checkAndSend, 100);
            setTimeout(() => clearInterval(interval), 5000);
        } else {
            this._connect();
            setTimeout(() => this._send(data), 1000);
        }
    }

    _connect() {
        this._socket = new WebSocket(this._address);
        this._socket.onopen = () => {
            // Re-register listeners on reconnect
            Object.keys(this._listeners).forEach(method => {
                this._listeners[method].forEach(callback => {
                    this.addListener(method, callback);
                });
            });
        };

        this._socket.onmessage = event => {
            try {
                const response = JSON.parse(event.data);
                this._onmessage(response, this._handles, this._listeners);
            } catch (e) {
                this._onerror(e, this._handles);
            }
        };

        this._socket.onclose = () => {
            this._onerror({ message: 'Connection closed' }, this._handles);
            setTimeout(() => this._connect(), 5000);
        };

        this._socket.onerror = error => {
            this._onerror(error, this._handles);
            this._socket.close();
        };
    }

    _onerror(error, handles) {
        Object.values(handles).forEach(handle => {
            if (handle.error) handle.error(error);
        });
        this._handles = {};
    }

    _onmessage(message, handles, listeners) {
        if (message.id && handles[message.id]) {
            const handle = handles[message.id];
            if (message.error) {
                if (handle.error) handle.error(message.error);
            } else if (message.result !== undefined) {
                if (handle.success) handle.success(message.result);
            }
            delete handles[message.id];
        } else if (message.method && listeners[message.method]) {
            listeners[message.method].forEach(callback => {
                callback(message.params);
            });
        }
    }
