function initializeWebSocket(handleFunctionCall, url, cb) {
    let socket;
    let reconnectAttempts = 0;
    const maxReconnectAttempts = 5;
    let shouldReconnect = true;

    function connect() {
        socket = new WebSocket(url);

        socket.onopen = () => {
            postMessage({ status: 'connected' });
            cb(socket);
            reconnectAttempts = 0;
        };

        socket.onmessage = (event) => {
            try {
                const message = JSON.parse(event.data);
                handleFunctionCall(message);
            } catch (e) {
                console.error('Error parsing message:', e);
            }
        };

        socket.onclose = (event) => {
            postMessage({ status: 'disconnected', reason: event.reason });
            if (shouldReconnect && reconnectAttempts < maxReconnectAttempts) {
                reconnectAttempts++;
                setTimeout(connect, Math.min(1000 * Math.pow(2, reconnectAttempts), 10000));
            }
        };

        socket.onerror = (error) => {
            socket.close();
        };
    }

    connect();

    return {
        getSocket: () => socket,
        disconnect: () => {
            shouldReconnect = false;
            if (socket) {
                socket.close();
            }
        },
        reconnect: () => {
            shouldReconnect = true;
            if (socket && socket.readyState === WebSocket.CLOSED) {
                connect();
            }
        }
    };
}

