class ReconnectingWebSocket {
  constructor(url, protocols, options = {}) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = 3; // CLOSED
    this.protocol = '';

    this._listeners = {
      open: [],
      close: [],
      connecting: [],
      message: [],
      error: []
    };

    this._ws = null;
    this._reconnectTimeout = null;
    this._connectTimeout = null;

    if (this.automaticOpen) {
      this.open();
    }
  }

  addEventListener(type, listener) {
    if (this._listeners[type]) {
      this._listeners[type].push(listener);
    }
  }

  removeEventListener(type, listener) {
    if (this._listeners[type]) {
      const index = this._listeners[type].indexOf(listener);
      if (index > -1) {
        this._listeners[type].splice(index, 1);
      }
    }
  }

  dispatchEvent(event) {
    const type = event.type;
    if (this._listeners[type]) {
      this._listeners[type].forEach(listener => {
        if (typeof listener === 'function') {
          listener.call(this, event);
        } else if (listener && typeof listener.handleEvent === 'function') {
          listener.handleEvent(event);
        }
      });
    }

    const handler = 'on' + type;
    if (typeof this[handler] === 'function') {
      this[handler].call(this, event);
    }
  }

  open(reconnecting = false) {
    if (!window.WebSocket) {
      throw new Error('WebSocket is not supported in this environment');
    }

    if (this.debug) {
      console.log(`RWS: Opening connection to ${this.url}`);
    }

    this.readyState = 0; // CONNECTING
    this.dispatchEvent({ type: 'connecting' });

    if (reconnecting) {
      this.reconnectAttempts++;
    } else {
      this.reconnectAttempts = 0;
    }

    try {
      this._ws = new WebSocket(this.url, this.protocols);

      this._ws.onopen = () => {
        if (this.debug) {
          console.log('RWS: Connection established');
        }

        this.readyState = 1; // OPEN
        this.reconnectAttempts = 0;
        this.protocol = this._ws.protocol || '';
        this.dispatchEvent({ type: 'open' });

        clearTimeout(this._connectTimeout);
      };

      this._ws.onclose = (event) => {
        if (this.debug) {
          console.log('RWS: Connection closed:', event);
        }

        this.readyState = 3; // CLOSED
        this.protocol = '';
        this.dispatchEvent({ type: 'close', code: event.code, reason: event.reason, wasClean: event.wasClean });

        if (!this._shouldStopReconnecting()) {
          this._scheduleReconnect();
        }
      };

      this._ws.onmessage = (event) => {
        this.dispatchEvent({ type: 'message', data: event.data });
      };

      this._ws.onerror = (event) => {
        if (this.debug) {
          console.error('RWS: Connection error:', event);
        }

        this.dispatchEvent({ type: 'error', error: event.error });
      };

      this._connectTimeout = setTimeout(() => {
        if (this.readyState === 0) { // Still connecting
          if (this.debug) {
            console.warn('RWS: Connection timeout');
          }

          this._ws.close(4408, 'Connection timeout');
        }
      }, this.timeoutInterval);

    } catch (error) {
      if (this.debug) {
        console.error('RWS: Connection error:', error);
      }

      this.dispatchEvent({ type: 'error', error: error });
      this.readyState = 3; // CLOSED

      if (!this._shouldStopReconnecting()) {
        this._scheduleReconnect();
      }
    }
  }

  send(data) {
    if (this.readyState === 1 && this._ws) { // OPEN
      try {
        this._ws.send(data);
        return;
      } catch (error) {
        if (this.debug) {
          console.error('RWS: Send error:', error);
        }
      }
    }

    if (this.debug) {
      console.warn('RWS: Cannot send data when connection is not open');
    }
  }

  close(code = 1000, reason = 'Normal closure') {
    if (this.debug) {
      console.log('RWS: Closing connection');
    }

    this._clearReconnect();

    if (this._ws) {
      this._ws.onclose = () => {}; // Clear existing handlers
      this._ws.close(code, reason);
    }

    this.readyState = 3; // CLOSED
  }

  refresh() {
    if (this.debug) {
      console.log('RWS: Refreshing connection');
    }

    if (this._ws) {
      this._ws.close(4100, 'Refreshing connection');
    }
  }

  onopen() {}
  onclose() {}
  onconnecting() {}
  onmessage() {}
  onerror() {}

  _scheduleReconnect() {
    this._clearReconnect();

    const reconnectDelay = Math.min(
      this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
      this.maxReconnectInterval
    );

    if (this.debug) {
      console.log(`RWS: Scheduling reconnect in ${reconnectDelay}ms (attempt ${this.reconnectAttempts + 1})`);
    }

    this._reconnectTimeout = setTimeout(() => {
      this.open(true);
    }, reconnectDelay);
  }

  _clearReconnect() {
    if (this._reconnectTimeout) {
      clearTimeout(this._reconnectTimeout);
      this._reconnectTimeout = null;
    }

    if (this._connectTimeout) {
      clearTimeout(this._connectTimeout);
      this._connectTimeout = null;
    }
  }

  _shouldStopReconnecting() {
    return this.readyState === 3 || this.readyState === 2;
  }
}

