        this.ws.onerror = (error) => {
            console.error('WebSocket error:', error);
            this.snack.error('Connection error. Reconnecting...');
            this.ws.close();
        };
    }

    close() {
        if (this.ws && this.wsActive) {
            this.ws.close(1000);
        }
    }
