class MonitorManagerProxyClientImpl {
  constructor() {
    this.webSocket = undefined;
    this.wsPort = undefined;
    this.eventListeners = {};
  }

  connect(addressPort) {
    return new Promise((resolve, reject) => {
      try {
        this.webSocket = new WebSocket(`ws://localhost:${addressPort}`);
        this.wsPort = addressPort;

        this.webSocket.onopen = () => resolve();

        this.webSocket.onmessage = (event) => {
          this._emit('messageReceived', event.data);
        };

        this.webSocket.onclose = () => {
          this.webSocket = undefined;
          this._emit('disconnected');
        };

        this.webSocket.onerror = (error) => {
          reject(error);
          this.disconnect();
        };
      } catch (error) {
        reject(error);
      }
    });
  }

  disconnect() {
    if (this.webSocket) {
      this.webSocket.close();
      this.webSocket = undefined;
    }
  }

  send(message) {
    if (this.webSocket && this.webSocket.readyState === WebSocket.OPEN) {
      const command = JSON.stringify({ type: 'command', payload: message });
      this.webSocket.send(command);
    }
  }

  changeSettings(settings) {
    if (this.webSocket && this.webSocket.readyState === WebSocket.OPEN) {
      const settingsMessage = JSON.stringify({ type: 'settings', payload: settings });
      this.webSocket.send(settingsMessage);
    }
  }

  isWSConnected() {
    return new Promise((resolve) => {
      resolve(this.webSocket !== undefined && this.webSocket.readyState === WebSocket.OPEN);
    });
  }

  getWebSocketPort() {
    return this.wsPort;
  }

  _emit(event, data) {
    if (this.eventListeners[event]) {
      this.eventListeners[event].forEach((callback) => callback(data));
    }
  }

  on(event, callback) {
    if (!this.eventListeners[event]) {
      this.eventListeners[event] = [];
    }
    this.eventListeners[event].push(callback);
  }

  off(event, callback) {
    if (this.eventListeners[event]) {
      this.eventListeners[event] = this.eventListeners[event].filter(cb => cb !== callback);
    }
  }
}

