startRecording = (data) => {
    const ws = new WebSocket(`ws://${data.host}:${data.port}`);
    let mediaRecorder;
    let audioChunks = [];
    let isServerReady = false;
    let isCapturing = false;

    ws.onopen = () => {
        ws.send(JSON.stringify({
            language: data.language,
            task: data.task,
            model_size: data.modelSize,
            use_vad: data.useVad
        }));
    };

    ws.onmessage = (event) => {
        if (typeof event.data === 'string') {
            try {
                const msg = JSON.parse(event.data);
                if (msg.status === 'ready') {
                    isServerReady = true;
                    if (isCapturing) ws.send(JSON.stringify({ status: 'start' }));
                } else if (msg.status === 'transcription') {
                    browser.runtime.sendMessage({ transcription: msg.text });
                }
            } catch (e) {
                console.error('Message parsing error:', e);
            }
        } else {
            audioChunks.push(event.data);
            if (audioChunks.length > 0 && isServerReady && isCapturing) {
                const blob = new Blob(audioChunks, { type: 'audio/webm' });
                const reader = new FileReader();
                reader.onload = () => {
                    ws.send(reader.result);
                    audioChunks = [];
                };
                reader.readAsArrayBuffer(blob);
            }
        }
    };

    ws.onerror = (error) => {
        console.error('WebSocket Error:', error);
        browser.runtime.sendMessage({ error: 'WebSocket connection error' });
    };

    ws.onclose = () => {
        if (mediaRecorder && mediaRecorder.state !== 'inactive') {
            mediaRecorder.stop();
        }
        browser.runtime.sendMessage({ status: 'disconnected' });
    };

    navigator.mediaDevices.getUserMedia({ audio: true })
        .then((stream) => {
            mediaRecorder = new MediaRecorder(stream);
            mediaRecorder.ondataavailable = (e) => {
                if (e.data.size > 0) {
                    audioChunks.push(e.data);
                }
            };
            mediaRecorder.onstart = () => {
                isCapturing = true;
                if (isServerReady) ws.send(JSON.stringify({ status: 'start' }));
            };
            mediaRecorder.onstop = () => {
                isCapturing = false;
                ws.send(JSON.stringify({ status: 'stop' }));
            };
            mediaRecorder.start(250);
        })
        .catch((err) => {
            console.error('Microphone access denied:', err);
            browser.runtime.sendMessage({ error: 'Microphone access denied' });
            ws.close();
        });

    browser.runtime.onMessage.addListener((request, sender, sendResponse) => {
        if (request.command === 'stop') {
            if (mediaRecorder && mediaRecorder.state !== 'inactive') {
                mediaRecorder.stop();
            }
            ws.close();
            sendResponse({ status: 'stopped' });
