    await new Promise((resolve, reject) => {
      const timeout = setTimeout(() => reject(new Error("Ping timeout")), 5000);
      ws.send(JSON.stringify({ type: "ping" }));

      const messageHandler = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === "pong") {
          clearTimeout(timeout);
          ws.removeEventListener("message", messageHandler);
          resolve();
        }
      };

      ws.addEventListener("message", messageHandler);
    });
  }

  send(socketId, msg, onMessage) {
    return new Promise((resolve, reject) => {
      const ws = this.webSockets[socketId];
      if (!ws) return reject(new Error("Socket not found"));

      const messageHandler = (event) => {
        try {
          const data = JSON.parse(event.data);
          if (onMessage) onMessage(data);

          if (data.type === 2) {
            ws.removeEventListener("message", messageHandler);
            resolve(data);
          }
        } catch (err) {
          ws.removeEventListener("message", messageHandler);
          reject(err);
        }
      };

      ws.addEventListener("message", messageHandler);

      ws.send(JSON.stringify(msg));
    });
  }

  async close(socketId) {
    const ws = this.webSockets[socketId];
    if (ws) {
      ws.close();
      delete this.webSockets[socketId];
    }
  }
}

