      try {
        const data = JSON.parse(event.data);

        if (data.type === "output") {
          this.appendConsole(data.text);
        } else if (data.type === "error") {
          this.appendConsole(data.text);
        } else if (data.type === "execution_result") {
          if (data.status === "ok") {
            this.appendConsole("Execution completed successfully.\n");
          } else if (data.status === "error") {
            this.appendConsole(data.traceback.join('\n') + '\n');
          }
        } else if (data.type === "debug_step") {
          this.lineno.current = data.lineno - this.lineno.offset;
          this.setLineGoThrough(this.lineno.current);
        } else if (data.type === "kernel_restarted") {
          this.restarting = false;
          this.resetEditor();
          this.appendConsole("Kernel restarted.\n");
        }

        if (this.pendingResolve && data.type !== "output" && data.type !== "debug_step") {
          this.pendingResolve(data);
          this.pendingResolve = null;
        }
      } catch (e) {
        console.error("Error parsing message:", e);
      }
    };

    this.ws.onclose = () => {
      this.wsOpen = false;
      this.ws = null;
    };
  }

  runPython(code) {
    return new Promise((resolve, reject) => {
      if (!this.wsOpen) {
        reject(new Error("WebSocket not open"));
        return;
      }

      this.running = true;
      this.resetConsole();

      this.pendingResolve = resolve;

      this.ws.send(JSON.stringify({
        type: "execute",
        code: code
      }));
    }).finally(() => {
      this.running = false;
    });
  }

  restartKernel() {
    if (!this.wsOpen) return;

    this.restarting = true;
    this.ws.send(JSON.stringify({ type: "restart_kernel" }));
  }

  stopDebugging() {
    if (!this.wsOpen) return;

    this.ws.send(JSON.stringify({ type: "interrupt" }));
  }

