import { app, BrowserWindow } from 'electron'
import Mitt from 'mitt'
import FlyType from 'flytype'

type Events = {
  message: string
}

export class CocogoatWebControl {
  port: number = 32333
  token: string = ''
  hwnd: number = 0
  version: string = ''
  ev: Mitt<Events> = Mitt()
  client: FlyType = new FlyType()
  ws: WebSocket | undefined

  constructor(_port?: number) {
    if (_port) this.port = _port
  }

  async authorize(): Promise<boolean> {
    return new Promise((resolve, reject) => {
      const window = BrowserWindow.getFocusedWindow()
      if (!window) {
        reject(new Error('No focused window found'))
        return
      }

      this.hwnd = window.getNativeId()
      const wsUrl = `ws://localhost:${this.port}/authorize?hwnd=${this.hwnd}`

      this.ws = new WebSocket(wsUrl)

      this.ws.onopen = () => {
        console.log('WebSocket connected')
      }

      this.ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data.toString())
          if (data.type === 'auth_token') {
            this.token = data.token
            resolve(true)
          } else {
            this.ev.emit('message', event.data.toString())
          }
        } catch (error) {
          reject(error)
        }
      }

      this.ws.onerror = (error) => {
        reject(error)
      }

      this.ws.onclose = () => {
        console.log('WebSocket closed')
      }
    })
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{status: number, body: any}> {
    if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
      throw new Error('WebSocket not connected')
    }

    return new Promise((resolve, reject) => {
      const requestId = Math.random().toString(36).substring(2, 15)

      const messageHandler = (response: string) => {
        try {
          const data = JSON.parse(response)
          if (data.id === requestId) {
            this.ev.off('message', messageHandler)
            resolve({
              status: data.status || 200,
              body: data.result
            })
          }
        } catch (error) {
          reject(error)
        }
      }

      this.ev.on('message', messageHandler)

      const payload = {
        id: requestId,
        method,
        path,
        querystring,
        token: this.token
      }

      this.ws!.send(JSON.stringify(payload))
    })
  }
}

