class WebSocketClient {
  constructor() {
    this.wsClient = null;
    this.sendQueue = [];
    this.pendingReconnect = null;
    this.onOpenCallback = null;
    this.onCloseCallback = null;
    this.onMessageCallback = null;
    this.isManuallyClosed = false;
  }

  fireSendQueue() {
    if (this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
      while (this.sendQueue.length > 0) {
        const { message, resolve, reject } = this.sendQueue.shift();
        try {
          this.wsClient.send(message);
          resolve();
        } catch (error) {
          reject(error);
        }
      }
    }
  }

  clearSendQueue() {
    while (this.sendQueue.length > 0) {
      const { reject } = this.sendQueue.shift();
      reject(new Error('Connection closed before message could be sent'));
    }
  }

  planToReconnect(rpcUrl, options) {
    if (this.pendingReconnect || this.isManuallyClosed) return;

    this.pendingReconnect = setTimeout(() => {
      this.pendingReconnect = null;
      this.reconnect(rpcUrl, options);
    }, 5000);
  }

  init() {
    if (this.pendingReconnect) {
      clearTimeout(this.pendingReconnect);
      this.pendingReconnect = null;
    }

    if (this.wsClient) {
      this.wsClient.onopen = null;
      this.wsClient.onclose = null;
      this.wsClient.onerror = null;
      this.wsClient.onmessage = null;

      if (this.wsClient.readyState === WebSocket.OPEN ||
          this.wsClient.readyState === WebSocket.CONNECTING) {
        try {
          this.wsClient.close();
        } catch (e) {
          // Ignore errors during close
        }
      }
      this.wsClient = null;
    }

    this.clearSendQueue();
  }

  connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
    this.init();

    this.onOpenCallback = onOpenCallback;
    this.onCloseCallback = onCloseCallback;
    this.onMessageCallback = onMessageCallback;
    this.isManuallyClosed = false;

    const { protocols = [] } = options || {};
    this.wsClient = new WebSocket(rpcUrl, protocols);

    this.wsClient.onopen = () => {
      if (this.onOpenCallback) {
        this.onOpenCallback();
      }
      this.fireSendQueue();
    };

    this.wsClient.onclose = (event) => {
      this.clearSendQueue();
      if (this.onCloseCallback) {
        this.onCloseCallback(event);
      }
      if (!this.isManuallyClosed) {
        this.planToReconnect(rpcUrl, options);
      }
    };

    this.wsClient.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.wsClient.close();
    };

    this.wsClient.onmessage = (event) => {
      if (this.onMessageCallback) {
        this.onMessageCallback(event);
      }
    };
  }

  reconnect(rpcUrl, options) {
    this.connect(
      rpcUrl,
      options,
      this.onOpenCallback,
      this.onCloseCallback,
      this.onMessageCallback
    );
  }

  send(requestContext) {
    return new Promise((resolve, reject) => {
      const message = JSON.stringify(requestContext);
      this.sendQueue.push({ message, resolve, reject });
      this.fireSendQueue();
    });
  }

  getReadyState() {
    return this.wsClient ? this.wsClient.readyState : null;
  }

  disconnect() {
    this.isManuallyClosed = true;
    this.init();
  }
}

