const WebSocket = require('ws');
class SnapdropServer {
    constructor(port) {
        this._wss = new WebSocket.Server({ port });
        this._rooms = {};
        this._wss.on('connection', this._onConnection.bind(this));
        this._wss.on('headers', this._onHeaders.bind(this));
    }
    _onConnection(peer) {
        this._joinRoom(peer);
        peer.on('message', (data) => this._onMessage(peer, data.toString()));
        peer.on('close', () => this._leaveRoom(peer));
        this._keepAlive(peer);
    }
    _onHeaders(headers, response) {
        const cookie = `session=${Date.now()}; Path=/; HttpOnly`;
        response.setHeader('Set-Cookie', [cookie]);
    }
    _onMessage(sender, message) {
        try {
            const parsed = JSON.parse(message);
            if (parsed.type === 'offer' || parsed.type === 'answer' || parsed.type === 'candidate') {
                const room = this._rooms[sender._ip];
                if (room) {
                    room.forEach(peer => {
                        if (peer !== sender && peer.readyState === WebSocket.OPEN) {
                            this._send(peer, parsed);
                        }
                    });
                }
            }
        } catch (e) {}
    }
    _send(peer, message) {
        if (peer.readyState === WebSocket.OPEN) {
            peer.send(JSON.stringify(message));
        }
    }
    _joinRoom(peer) {
        const ip = peer._socket.remoteAddress;
        peer._ip = ip;
        if (!this._rooms[ip]) {
            this._rooms[ip] = new Set();
        }
        this._rooms[ip].add(peer);
    }
    _leaveRoom(peer) {
        const ip = peer._ip;
        if (this._rooms[ip]) {
            this._rooms[ip].delete(peer);
            if (this._rooms[ip].size === 0) {
                delete this._rooms[ip];
            }
        }
    }
    _keepAlive(peer) {
        const interval = setInterval(() => {
            if (peer.readyState === WebSocket.OPEN) {
                peer.ping();
            }
        }, 30000);
        peer.on('close', () => clearInterval(interval));
    }
}

