class XVIZServer {
  constructor(handlers, options = {}, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('At least one handler must be provided');
    }

    this.handlers = handlers;
    this.options = {
      port: 8080,
      logger: console,
      ...options
    };

    this._server = new WebSocket.Server({ port: this.options.port }, callback);

    this._server.on('connection', (socket, request) => {
      this.handleSession(socket, request).catch(err => {
        this.log('Error handling session:', err);
        socket.close();
      });
    });

    this._server.on('error', error => {
      this.log('WebSocket server error:', error);
    });
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    const sessionId = uuidv4();
    let accepted = false;

    try {
      for (const handler of this.handlers) {
        if (await handler.canHandle(request)) {
          accepted = true;
          handler.handle(socket, request, sessionId);
          break;
        }
      }

      if (!accepted) {
        socket.close();
      }
    } catch (err) {
      this.log('Error in session handling:', err);
      if (!accepted && socket.readyState === WebSocket.OPEN) {
        socket.close();
      }
    }
  }

  log(...msg) {
    if (this.options.logger) {
      this.options.logger.log(...msg);
    }
  }
}

module.exports = XVIZServer;

