      username: null,
      password: null
    }, ws);

    ws.on('message', function incoming(message) {
      client.handle(message);
    });

    client.on('publish', (packet) => {
      aedes.publish(packet);
    });

    client.on('subscribe', (subscriptions) => {
      client.subscriptions = client.subscriptions.concat(subscriptions);
    });

    client.on('unsubscribe', (unsubscriptions) => {
      client.subscriptions = client.subscriptions.filter((s) =>
        !unsubscriptions.some((u) => u.topic === s.topic)
