const WebSocket = require('ws');
const http = require('http');

class AphlictClientServer {
  constructor(server) {
    this._server = server;
    this._lists = {};
    this._adminServers = [];
  }

  _onrequest(request, response) {
    response.writeHead(501);
    response.end();
  }

  _parseInstanceFromPath(path) {
    const match = path.match(/^\/([^?]+)/);
    return match ? match[1] : null;
  }

  listen() {
    const wss = new WebSocket.Server({ server: this._server });

    wss.on('connection', (socket, request) => {
      const instance = this._parseInstanceFromPath(request.url);
      if (!instance) {
        socket.close();
        return;
      }

      this.log(`Connection established for instance ${instance}`);

      const listeners = this.getListenerList(instance);
      listeners[socket] = { socket, subscriptions: {} };

      socket.on('message', (data) => {
        try {
          const message = JSON.parse(data.toString());

          if (message.type === 'subscribe') {
            listeners[socket].subscriptions[message.channel] = true;
            this.trace(`Socket subscribed to ${message.channel}`);
          } 
          else if (message.type === 'unsubscribe') {
            delete listeners[socket].subscriptions[message.channel];
            this.trace(`Socket unsubscribed from ${message.channel}`);
          }
          else if (message.type === 'replay') {
            const history = this.getHistory(message.min_age || 0);
            history.forEach(msg => {
              if (message.channels.includes(msg.channel)) {
                socket.send(JSON.stringify(msg));
              }
            });
          }
          else if (message.type === 'ping') {
            socket.send(JSON.stringify({ type: 'pong' }));
          }
        } catch (e) {
          this.log(`Error processing message: ${e.message}`);
        }
      });

      socket.on('close', () => {
        delete listeners[socket];
        this.log(`Connection closed for instance ${instance}`);
      });
    });
  }

  getListenerList(instance) {
    if (!this._lists[instance]) {
      this._lists[instance] = {};
    }
    return this._lists[instance];
  }

  log(message) {
    console.log(`[AphlictServer] ${message}`);
  }

  trace(message) {
    if (process.env.DEBUG) {
      console.log(`[DEBUG] ${message}`);
    }
  }

  getHistory(min_age) {
    // This would typically query a persisted store of messages
    // For now we'll return an empty array as placeholder
    return [];
  }
}

