class HTTPClient {
  constructor() {
    this.requestModule = 'axios';
  }

  async httpRequest(options) {
    const axios = require('axios');
    const config = {
      method: options.method.toLowerCase(),
      url: options.url,
      headers: options.headers || {},
      params: options.params || null,
    };

    if (config.method === 'post') {
      if (!config.headers['Content-Type']) {
        config.headers['Content-Type'] = 'application/json';
      }

      if (config.headers['Content-Type'].includes('application/json')) {
        config.data = typeof options.body === 'string' && this.isJSONString(options.body)
          ? JSON.parse(options.body)
          : options.body;
      } else if (config.headers['Content-Type'].includes('application/x-www-form-urlencoded')) {
        config.data = typeof options.body === 'object'
          ? new URLSearchParams(options.body).toString()
          : options.body;
      } else {
        config.data = options.body;
      }
    }

    try {
      const response = await axios(config);
      return {
        status: response.status,
        headers: response.headers,
        data: response.data,
      };
    } catch (error) {
      throw {
        status: error.response?.status,
        headers: error.response?.headers,
        data: error.response?.data,
        message: error.message,
      };
    }
  }

  queryStr(options) {
    return new URLSearchParams(options).toString();
  }

  isJSONString(str) {
    try {
      JSON.parse(str);
      return true;
    } catch (e) {
      return false;
    }
  }

  isJson(obj) {
    return obj !== null && !Array.isArray(obj) && typeof obj === 'object';
  }
}

