const axios = require('axios');

function sendMail(to, subject, text, options = {}) {
  const transporter = {
    sendMail: (mailOptions) => {
      return new Promise((resolve, reject) => {
        const provider = mailOptions.provider; // 'mailgun' or 'sendgrid'
        const apiKey = process.env[`${provider.toUpperCase()}_API_KEY`];
        const domain = process.env[`${provider.toUpperCase()}_DOMAIN`];

        if (!apiKey || !domain) {
          return reject(new Error(`Missing API credentials for ${provider}`));
        }

        let requestConfig;

        if (provider === 'mailgun') {
          const formData = new URLSearchParams();
          formData.append('from', mailOptions.from || `noreply@${domain}`);
          formData.append('to', mailOptions.to);
          formData.append('subject', mailOptions.subject);
          formData.append('text', mailOptions.text);

          requestConfig = {
            method: 'post',
            url: `https://api.mailgun.net/v3/${domain}/messages`,
            auth: { username: 'api', password: apiKey },
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            data: formData
          };
        } else if (provider === 'sendgrid') {
          const emailData = {
            personalizations: [{
              to: [{ email: mailOptions.to }],
              subject: mailOptions.subject
            }],
            from: { email: mailOptions.from || `noreply@${domain}` },
            content: [{
              type: 'text/plain',
              value: mailOptions.text
            }]
          };

          requestConfig = {
            method: 'post',
            url: 'https://api.sendgrid.com/v3/mail/send',
            headers: {
              'Authorization': `Bearer ${apiKey}`,
              'Content-Type': 'application/json'
            },
            data: JSON.stringify(emailData)
          };
        } else {
          return reject(new Error(`Unsupported email provider: ${provider}`));
        }

        axios(requestConfig)
          .then(response => {
            resolve({ messageId: response.headers['x-message-id'] || response.data.message || 'Unknown ID' });
          })
          .catch(error => {
            reject(new Error(`Failed to send email via ${provider}: ${error.message}`));
          });
      });
    }
  };

  const mailOptions = {
    to,
    subject,
    text,
    ...options
  };

  return transporter.sendMail(mailOptions);
}

