const axios = require('axios');

function downloadIcons(url, style, callback) {
  const targetImagePath = path.join(__dirname, 'icons', style);
  const duplicateLogPath = path.join(__dirname, 'duplicate_icons.log');
  const defaultHeaders = {
    Accept: 'application/json',
    'X-Requested-With': 'XMLHttpRequest',
    withCredentials: true
  };

  if (!fs.existsSync(targetImagePath)) {
    fs.mkdirSync(targetImagePath, { recursive: true });
  }

  let downloadedIcons = [];
  let duplicatesFound = [];

  axios.get(url, { headers: defaultHeaders })
    .then(response => {
      const iconData = response.data;

      if (!Array.isArray(iconData)) {
        throw new Error('Unexpected data format received from server.');
      }

      return Promise.all(iconData.map(icon => {
        const iconName = icon.name || `icon_${Date.now()}`;
        const svgContent = icon.svg ? icon.svg.trim() : '';
        const existingIcon = downloadedIcons.find(i => i.name === iconName);

        if (existingIcon) {
          duplicatesFound.push(iconName);
          fs.appendFileSync(duplicateLogPath, `${new Date().toISOString()} - Duplicate icon found: ${iconName}\n`);
          return Promise.resolve(null);
        }

        const filePath = path.join(targetImagePath, `${iconName}.svg`);

        return new Promise((resolve, reject) => {
          fs.writeFile(filePath, svgContent, err => {
            if (err) {
              return reject(err);
            }
            downloadedIcons.push({
              name: iconName,
              svg: filePath,
              category: icon.category || 'uncategorized',
              style: style,
              tags: Array.isArray(icon.tags) ? icon.tags : [],
              code: icon.code || null,
              unicode: icon.unicode || null,
              pro: !!icon.pro
            });
            resolve();
          });
        });
      }));
    })
    .then(() => {
      downloadedIcons.sort((a, b) => a.name.localeCompare(b.name));
      callback(null, downloadedIcons);
    })
    .catch(error => {
      console.error('Error during icon download or processing:', error.message);
      callback(error);
    });
}

