            if (timedOut) {
                return;
            }
            clearTimeout(timer);
            resolve(xhr);
        };
        xhr.onerror = (error): void => {
            if (timedOut) {
                return;
            }
            clearTimeout(timer);
            reject(error);
        };
        let body = options.body;
        if (typeof (body) === 'object' && !isArrayBufferView(body) && body instanceof ArrayBuffer) {
            body = new Uint8Array(body);
        }
        xhr.send(body);
    });
}

