    this._contentLength = null;
    this._receivedLength = 0;
    this._dataSource = null;
    this._range = null;
    this._requestURL = null;

    this._onURLRedirect = null;
    this._onError = null;
    this._onContentLengthKnown = null;
    this._onDataArrival = null;
    this._onComplete = null;
