      if (contentRange) {
        const totalMatch = contentRange.match(/\/(\d+)$/);
        if (totalMatch) {
          this._contentLength = parseInt(totalMatch[1], 10);
          if (typeof this._onContentLengthKnown === 'function') {
            this._onContentLengthKnown(this._contentLength);
          }
        }
      }
    } else if (xhr.readyState === XMLHttpRequest.DONE) {
      if (xhr.status >= 300 && xhr.status < 400 && xhr.getResponseHeader('Location')) {
        const redirectUrl = xhr.getResponseHeader('Location');
        if (typeof this._onURLRedirect === 'function') {
          this._onURLRedirect(redirectUrl);
        }
        return;
      }

      if (xhr.status >= 200 && xhr.status < 300) {
