const cache = {};

function getNote(url, instrument, name, audioContext) {
  const key = `${instrument}-${name}`;
  if (cache[key]) {
    return Promise.resolve(cache[key]);
  }

  return new Promise((resolve, reject) => {
    const request = new XMLHttpRequest();
    request.open('GET', `${url}/${instrument}/${name}.mp3`, true);
    request.responseType = 'arraybuffer';

    request.onload = () => {
      if (request.status === 200) {
        audioContext.decodeAudioData(
          request.response,
          (audioBuffer) => {
            const result = {
              instrument,
              name,
              status: 'loaded',
              audioBuffer
            };
            cache[key] = result;
            resolve(result);
          },
          (decodeError) => {
            reject(new Error(`Failed to decode audio data for ${instrument} ${name}: ${decodeError.message}`));
          }
        );
      } else {
        reject(new Error(`Failed to load audio file for ${instrument} ${name}: HTTP status ${request.status}`));
      }
    };

    request.onerror = () => {
      reject(new Error(`Network error while loading audio file for ${instrument} ${name}`));
    };

    request.send();
  });
}

