import { RequestMethod } from 'umi-request';
import { IRequestService, TRequestOption } from './types';

class BasicRequestService implements IRequestService {
  private requestMethod: RequestMethod;

  constructor(requestMethod: RequestMethod) {
    this.requestMethod = requestMethod;
  }

  async download(url: string): Promise<Blob> {
    const hasPermission = await this.checkPermissions();
    if (!hasPermission) {
      throw new Error('Download permission denied');
    }

    return new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.open('GET', url, true);
      xhr.responseType = 'blob';

      xhr.onload = () => {
        if (xhr.status === 200) {
          resolve(xhr.response);
        } else {
          reject(new Error(`Failed to download file, status code: ${xhr.status}`));
        }
      };

      xhr.onerror = () => {
        reject(new Error('Network error occurred during download'));
      };

      xhr.send();
    });
  }

  async request(url: string, options: TRequestOption): Promise<any> {
    const { method, headers, data, requestType } = options;

    switch (method.toLowerCase()) {
      case 'get':
        return this.requestMethod.get(url, { headers, params: data });
      case 'put':
        return this.requestMethod.put(url, data, { headers });
      case 'post':
        if (requestType === 'form') {
          return this.requestMethod.post(url, data, { headers, requestType: 'form' });
        } else if (requestType === 'json') {
          return this.requestMethod.post(url, data, { headers, requestType: 'json' });
        } else {
          return this.requestMethod.post(url, data, { headers });
        }
      default:
        throw new Error(`Unsupported HTTP method: ${method}`);
    }
  }

  private async checkPermissions(): Promise<boolean> {
    try {
      const response = await fetch('/api/check-download-permissions', { method: 'GET' });
      const result = await response.json();
      return result.hasPermission === true;
    } catch (error) {
      console.error('Error checking permissions:', error);
      return false;
    }
  }
}

