function handleFormSubmit(event) {
    event.preventDefault();
    const form = event.target;
    const buttons = form.querySelectorAll("button");
    buttons.forEach(button => button.disabled = true);

    const honeypot = form.querySelector("[name='honeypot']");
    if (honeypot && honeypot.value.trim() !== "") {
        form.reset();
        buttons.forEach(button => button.disabled = false);
        return;
    }

    const formData = new FormData(form);
    const xhr = new XMLHttpRequest();
    xhr.open("POST", form.action, true);
    xhr.setRequestHeader("Accept", "application/json");

    xhr.onload = function () {
        buttons.forEach(button => button.disabled = false);
        if (xhr.status === 200) {
            form.reset();
            const thankYouMessage = document.createElement("div");
            thankYouMessage.textContent = "Thank you for your submission!";
            thankYouMessage.style.marginTop = "1rem";
            thankYouMessage.style.color = "green";
            form.appendChild(thankYouMessage);
        }
    };

    xhr.onerror = function () {
        buttons.forEach(button => button.disabled = false);
    };

    xhr.send(formData);
}

