const bodyParser = require('body-parser');
const connectRest = require('connect-rest');
const mongoose = require('mongoose');
const domain = require('domain');
const http = require('http');
const cluster = require('cluster');
const numCPUs = require('os').cpus().length;

if (cluster.isMaster) {
  for (let i = 0; i < numCPUs; i++) {
    cluster.fork();
  }

  cluster.on('exit', (worker, code, signal) => {
    console.log(`Worker ${worker.process.pid} died`);
    cluster.fork();
  });
} else {
  const app = express();
  const PORT = process.env.PORT || 3000;

  // Middleware
  app.use(bodyParser.json());

  // Connect to MongoDB
  mongoose.connect('mongodb://localhost/attractionsDB', { useNewUrlParser: true, useUnifiedTopology: true });
  const db = mongoose.connection;
  db.on('error', console.error.bind(console, 'MongoDB connection error:'));

  // Attraction Schema
  const attractionSchema = new mongoose.Schema({
    name: String,
    description: String,
    location: String,
    email: String,
    approved: { type: Boolean, default: false }
  });

  const Attraction = mongoose.model('Attraction', attractionSchema);

  // REST API Configuration
  const restOptions = {
    context: '/api',
    versionPath: false
  };

  const api = connectRest.createClient(restOptions);

  // Routes
  api.get('/attractions', async (req, res, next) => {
    try {
      const attractions = await Attraction.find({ approved: true });
      res.send(attractions);
    } catch (err) {
      next(err);
    }
  });

  api.post('/attraction', async (req, res, next) => {
    try {
      const { name, description, location, email } = req.body;
      if (!name || !description || !location || !email) {
        return res.status(400).send({ error: 'Missing required fields' });
      }

      const newAttraction = new Attraction({ name, description, location, email });
      const saved = await newAttraction.save();
      res.status(201).send({ id: saved._id });
    } catch (err) {
      next(err);
    }
  });

  api.get('/attraction/:id', async (req, res, next) => {
    try {
      const attraction = await Attraction.findById(req.params.id);
      if (!attraction) {
        return res.status(404).send({ error: 'Attraction not found' });
      }
      res.send(attraction);
    } catch (err) {
      next(err);
    }
  });

  app.use('/api', api.handler());

  // Error handling middleware
  app.use((err, req, res, next) => {
    console.error('Error:', err.stack);
    res.status(500).send({ error: 'Something went wrong!' });
  });

  // Domain-based error handling
  const serverDomain = domain.create();
  serverDomain.on('error', (err) => {
    console.error('Domain error:', err.stack);
    try {
      setTimeout(() => {
        process.exit(1);
      }, 5000).unref();
    } catch (e) {
      console.error('Failed to shut down server gracefully:', e);
      process.exit(1);
    }
  });

  serverDomain.run(() => {
    const server = http.createServer(app);
    server.listen(PORT, () => {
      console.log(`Server running on port ${PORT} (Worker PID: ${process.pid})`);
    });

    module.exports = server;
  });
}

