        const filteredHeaders = {};
        const forbiddenHeaders = ['x-secret-token', 'x-internal'];

        Object.keys(req.headers).forEach(header => {
            if (!forbiddenHeaders.includes(header.toLowerCase())) {
                filteredHeaders[header] = req.headers[header];
            }
