            .then(res => {
                const writer = tar.x({ strip: 1, C: this.binaryDirectory });

                return new Promise((resolve, reject) => {
                    res.data.pipe(writer);
                    let error = null;
                    writer.on('error', err => {
                      error = err;
                      reject(err);
                    });
                    writer.on('close', () => {
                      if (!error) {
                        resolve(true);
                      }
                    });
                })
            })
            .then(() => {
                console.log(
                    `${this.name ? this.name : "Your package"} has been installed!`
                );
            })
            .catch(e => {
                error(`Error fetching release: ${e.message}`);
            });
    }
}

