        this._isSurge = typeof $httpClient != "undefined"
        this._isQuanX = typeof $task != "undefined"
        this.isSurge = this._isSurge
        this.isQuanX = this._isQuanX
        this.isResponse = typeof $response != "undefined"
    }

    notify(title, subtitle, message, option) {
        if (this._isQuanX) $notify(title, subtitle, message, option)
        if (this._isSurge) $notification.post(title, subtitle, message, option["open-url"])
        if (this._node) console.log(JSON.stringify({ title, subtitle, message }));
    }

    write(value, key) {
        if (this._isQuanX) return $prefs.setValueForKey(value, key)
        if (this._isSurge) return $persistentStore.write(value, key)
    }

    read(key) {
        if (this._isQuanX) return $prefs.valueForKey(key)
        if (this._isSurge) return $persistentStore.read(key)
    }

