function setupExpressServer() {
  const app = express();
  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));
  app.use(cookieParser());

  app.post('/auth', (req, res) => {
    const { username, password } = req.body;
    if (username === auth.username && password === auth.password) {
      res.status(200).send('Authenticated');
    } else {
      res.status(401).send('Authentication failed');
    }
  });

  app.post('/send-test-email', async (req, res) => {
    const { to, subject, text } = req.body;
    const transporter = nodemailer.createTransport({
      host: processInfo.emailHost,
      port: processInfo.emailPort,
      secure: true,
      auth: {
        user: processInfo.emailUser,
        pass: processInfo.emailPass,
      },
    });
    try {
      await transporter.sendMail({ to, subject, text });
      res.send('Test email sent successfully');
    } catch (error) {
      res.status(500).send('Failed to send test email');
    }
  });

  app.post('/save-config', (req, res) => {
    const config = req.body;
    lutil.saveConfig(config);
    res.send('Configuration saved');
  });

  app.listen(processInfo.port, () => {
    console.log(`Express server running on port ${processInfo.port}`);
  });
}

module.exports = setupExpressServer;

