const http = require('http');
const fs = require('fs').promises;
const path = require('path');
const send = require('send');

class Server {
  constructor(config) {
    this.config = config;
    this.path = config.path;
    this.sockets = [];
    this._http = null;
  }

  updateServer() {
    this._http = http.createServer(async (req, res) => {
      try {
        const url = new URL(req.url, `http://${req.headers.host}`);
        const filePath = path.resolve(this.path, url.pathname);

        if (!filePath.startsWith(this.path)) {
          res.statusCode = 403;
          res.end('Forbidden');
          return;
        }

        try {
          const stats = await fs.stat(filePath);
          if (stats.isDirectory()) {
            if (!url.pathname.endsWith('/')) {
              res.writeHead(301, { Location: url.pathname + '/' });
              res.end();
              return;
            }

            const indexPath = path.join(filePath, 'index.html');
            try {
              await fs.stat(indexPath);
              const fileStream = fs.createReadStream(indexPath);
              res.writeHead(200, { 'Content-Type': 'text/html' });
              fileStream.pipe(res);
              return;
            } catch (err) {
              const files = await fs.readdir(filePath);
              const html = `<html><body><ul>${files.map(file => `<li><a href="${file}">${file}</a></li>`).join('')}</ul></body></html>`;
              res.writeHead(200, { 'Content-Type': 'text/html' });
              res.end(html);
              return;
            }
          } else {
            const sendStream = send(req, filePath, { root: this.path });
            sendStream.on('error', (err) => {
              res.statusCode = 404;
              res.end('File not found');
            });
            sendStream.pipe(res);
            return;
          }
        } catch (err) {
          res.statusCode = 404;
          res.end('File not found');
        }
      } catch (err) {
        res.statusCode = 500;
        res.end('Internal Server Error');
      }
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        const index = this.sockets.indexOf(socket);
        if (index !== -1) {
          this.sockets.splice(index, 1);
        }
      });
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port, () => {
      console.log(`Server running on port ${this.config.port}`);
      callback();
    });
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => socket.destroy());
      this.sockets = [];
      callback();
    });
  }
}

module.exports = Server;

