const express = require('express');
const axios = require('axios');

class OAuthServer {
  constructor(options) {
    this.options = options;
    this.prompt = options.prompt;
    this.app = express();
    this.server = null;
    this.token = null;

    this.app.get('/callback', async (req, res) => {
      const code = req.query.code;
      if (!code) {
        return res.status(400).send('No code provided');
      }

      try {
        const token = await this.exchangeCodeForToken(code);
        this.token = token;
        this.prompt.resolve(token);
        res.send('Authentication successful! You can close this window.');
      } catch (err) {
        console.error('Error exchanging code:', err);
        res.status(500).send('Authentication failed');
      }
    });
  }

  async start() {
    return new Promise((resolve, reject) => {
      this.server = this.app.listen(this.options.port, () => {
        console.log(`OAuth server running on port ${this.options.port}`);
        resolve();
      }).on('error', reject);
    });
  }

  stop() {
    if (this.server) {
      this.server.close();
      this.server = null;
    }
  }

  async exchangeCodeForToken(code) {
    const params = new URLSearchParams();
    params.append('client_id', this.options.clientID);
    params.append('client_secret', this.options.clientSecret);
    params.append('code', code);

    try {
      const response = await axios.post('https://github.com/login/oauth/access_token', params, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });

      return response.data.access_token;
    } catch (error) {
      throw new Error(`Failed to exchange code: ${error.message}`);
    }
  }
}

module.exports = OAuthServer;

