    });
    socket.on('end', () => {
      checkData(receive, password, commandHandler);
      socket.destroy();
    });
    socket.on('error', (err) => {
      console.error('Socket error:', err);
    });
  });
  server.listen(port, host, () => {
    console.log(`Server listening on ${host}:${port}`);
  });
  return server;
}

function receiveData(receive, data) {
  receive.data = Buffer.concat([receive.data, data]);
}

function checkData(receive, password, commandHandler) {
  const data = receive.data;
  try {
    const json = JSON.parse(data.toString());
    const { timestamp, code, command } = json;
    if (checkCode(Buffer.from(timestamp), password, Buffer.from(code))) {
      commandHandler(command, code).then((response) => {
        const packet = pack({ code: response.code, data: response.data });
        receive.socket.write(packet);
      }).catch((err) => {
        console.error('Command handling error:', err);
      });
    } else {
      const errorPacket = pack({ code: 'ERROR', data: 'Invalid authentication' });
      receive.socket.write(errorPacket);
    }
  } catch (err) {
    console.error('Error parsing data:', err);
    const errorPacket = pack({ code: 'ERROR', data: 'Invalid data format' });
    receive.socket.write(errorPacket);
  }
}

function checkCode(data, password, code) {
  const hash = crypto.createHash('md5').update(data.toString() + password).digest();
  return hash.equals(code);
}

function pack(data) {
  const codeBuffer = Buffer.from(data.code);
  const dataBuffer = data.data ? Buffer.from(data.data) : Buffer.alloc(0);
  return Buffer.concat([codeBuffer, dataBuffer]);
}
