class MockServer {
  constructor(port, host, options) {
    this.host = host;
    this.port = port;
    this.family = options.family || 'ipv4';
    this.tlsEnabled = options.tls ? true : false;
    this.state = 'stopped';
    this.connections = 0;
    this.sockets = [];
    this.server = null;
    this.options = options;
  }

  start() {
    return new Promise((resolve, reject) => {
      if (this.state !== 'stopped') {
        return reject(new Error('Server already started'));
      }
      this.state = 'running';
      const tlsOptions = this.tlsEnabled ? this.options.tls : undefined;
      this.server = this.tlsEnabled
        ? require('tls').createServer(tlsOptions, (socket) => {
            this.sockets.push(socket);
            this.connections++;
            socket.on('close', () => {
              this.sockets = this.sockets.filter(s => s !== socket);
              this.connections--;
            });
          })
        : require('net').createServer((socket) => {
            this.sockets.push(socket);
            this.connections++;
            socket.on('close', () => {
              this.sockets = this.sockets.filter(s => s !== socket);
              this.connections--;
            });
          });

      this.server.on('error', (err) => {
        this.state = 'stopped';
        reject(err);
      });

      this.server.on('listening', () => {
        resolve();
      });

      this.server.listen({
        port: this.port,
        host: this.host,
        family: this.family
      });
    });
  }

  destroy() {
    return new Promise((resolve, reject) => {
      if (this.state !== 'running') {
        return reject(new Error('Server not running'));
      }
      this.state = 'destroyed';
      const closePromises = this.sockets.map(socket => {
        return new Promise((res, rej) => {
          socket.on('close', () => {
            res();
          });
          socket.end();
        });
      });
      Promise.all(closePromises).then(() => {
        this.server.close(() => {
          this.state = 'destroyed';
          resolve();
        });
      }).catch(reject);
    });
  }
}
