class Server {
  constructor(options, origins) {
    this.origins = origins || [];
    this.port = options.port || 843;
    this.log = options.log || console.log;
    this.socket = null;
    this.server = options.server || null;
    this.buffer = null;
  }

  listen(port, server, cb) {
    this.port = port || this.port;
    this.server = server || this.server;
    this.socket = net.createServer((socket) => {
      this.responder(socket);
    });
    this.socket.on('error', (err) => {
      this.log(`Server error: ${err.message}`);
    });
    this.socket.listen(this.port, () => {
      this.log(`Server listening on port ${this.port}`);
      if (cb) cb();
    });
    return this;
  }

  responder(socket) {
    this.compile();
    socket.write(this.buffer);
    socket.end();
    socket.destroy();
  }

  compile() {
    let xml = '<cross-domain-policy>\n';
    xml += '  <site-control permitted-cross-domain-policies="all"/>\n';
    this.origins.forEach(origin => {
      const [domain, port] = origin.split(':');
      xml += `  <allow-access-from domain="${domain}" port="${port}"/>\n`;
    });
    xml += '</cross-domain-policy>';
    this.buffer = Buffer.from(xml, 'utf-8');
  }

  add(origin) {
    if (Array.isArray(origin)) {
      this.origins = this.origins.concat(origin);
    } else {
      this.origins.push(origin);
    }
    return this;
  }

  remove(origin) {
    this.origins = this.origins.filter(o => o !== origin);
    return this;
  }

