            if (!weak) {
                weak = require("weak");
            }

            opts.proto = {};
            opts.proto.wrap = function (cb, id) {
                var proto = this;
                return weak(cb, function () {
                    proto.cull(id);
                });
            };
            opts.proto.unwrap = function (ref, id) {
                var cb = weak.get(ref);
                return cb || function () {};
            };
        }

