            this.server.emit('local', ref, d);
        });

        d.on('remote', (remote) => {
            this.server.emit('remote', remote, d);
        });

        stream.on('error', (err) => {
            if (err && err.code === 'EPIPE') return;
            d.emit('error', err);
        });

        d.stream = stream;
        stream.pipe(d);
        d.pipe(stream);
