const net = require('net');

class RedisInterceptor {
  constructor(Pythagora, listenPort, targetPort, intermediateData) {
    this.Pythagora = Pythagora;
    this.listenPort = listenPort;
    this.targetPort = targetPort || 6379;
    this.intermediateData = intermediateData;
    this.listenSocket = null;
    this.mode = undefined;
  }

  async init() {
    this.listenSocket = net.createServer((connection) => {
      connection.on('data', (data) => {
        this.processClientData(connection, data);
      });
    });

    return new Promise((resolve, reject) => {
      this.listenSocket.listen(this.listenPort, () => {
        resolve();
      });
    });
  }

  processClientData(connection, data) {
    if (this.mode === 'test') {
      const mockResponse = this.findMockResponse(data);
      if (mockResponse) {
        connection.write(mockResponse);
        return;
      }
    }

    this.forwardData(connection, data, this.mode === 'capture');
  }

  forwardData(connection, data, saveData) {
    const targetSocket = net.createConnection(this.targetPort, '127.0.0.1', () => {
      targetSocket.write(data);
    });

    targetSocket.on('data', (response) => {
      const chunks = this.splitIntoChunks(response.toString(), CHUNK_SIZE);
      chunks.forEach(chunk => {
        connection.write(chunk);
      });
      if (saveData) {
        this.Pythagora.saveData(data, response);
      }
    });

    targetSocket.on('close', () => {
      targetSocket.destroy();
    });

    targetSocket.on('error', (err) => {
      console.error('Target socket error:', err);
      connection.destroy();
    });
  }

  findMockResponse(request) {
    for (const entry of this.intermediateData) {
      if (entry.request.toString() === request.toString()) {
        return entry.response;
      }
    }
    return null;
  }

  splitIntoChunks(str, chunkSize) {
    const chunks = [];
    for (let i = 0; i < str.length; i += chunkSize) {
      chunks.push(str.slice(i, i + chunkSize));
    }
    return chunks;
  }
}

