const net = require('net');
const EventEmitter = require('events');

class Node extends EventEmitter {
  static PORT = 3000;

  constructor(id) {
    super();
    this.id_ = id;
    this.peers_ = {};
    this.server_ = net.createServer();
    this.server_.on('connection', (socket) => {
      socket.on('data', (data) => {
        this.on_data(data, socket);
      });
      socket.on('close', () => {
        this.remove_peer(socket);
      });
    });
  }

  start() {
    const port = Node.PORT + this.id_;
    return new Promise((resolve, reject) => {
      this.server_.listen(port, () => {
        console.log(`Node ${this.id_} started on port ${port}`);
        resolve();
      });
    });
  }

  on_data(data, socket) {
    const message = data.toString();
    if (!this.peers_[message]) {
      this.add_peer(socket, message);
      this.emit('peer_connected', message);
    } else {
      this.emit('message', message, socket);
    }
  }

  send(socketOrId, data) {
    if (socketOrId instanceof net.Socket) {
      socketOrId.write(data);
    } else {
      const socket = this.peers_[socketOrId];
      if (socket) {
        socket.write(data);
      }
    }
  }

  broadcast(data) {
    for (const socket of Object.values(this.peers_)) {
      socket.write(data);
    }
  }

  add_peer(socket, remote_id) {
    this.peers_[remote_id] = socket;
  }

  remove_peer(socket) {
    for (const id in this.peers_) {
      if (this.peers_[id] === socket) {
        delete this.peers_[id];
        break;
      }
    }
  }

  list_peers() {
    return Object.keys(this.peers_);
  }
}

module.exports = Node;
