function setupUDPServer(argv, io, debug) {
  const streams = new Map();
  const server = dgram.createSocket('udp4');

  server.on('message', (msg, rinfo) => {
    try {
      const data = JSON.parse(msg.toString());
      const streamId = data.stream;
      if (!streams.has(streamId)) {
        streams.set(streamId, []);
      }
      const stream = streams.get(streamId);
      stream.push(data);
      if (stream.length > 100) {
        stream.shift();
      }
      io.emit('message', { stream: streamId, message: data });
      const streamsList = Array.from(streams.keys());
      io.emit('streams', streamsList);
    } catch (err) {
      debug(`Error parsing JSON: ${err.message}`);
    }
  });

