const dgram = require('dgram');
const os = require('os');

class ServerDiscovery {
  constructor() {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = 0;
    this.broadcastIP = this.calcBroadcastIP();
    this.discoveryListener = null;
  }

  calcBroadcastIP() {
    const interfaces = os.networkInterfaces();
    for (const name of Object.keys(interfaces)) {
      for (const iface of interfaces[name]) {
        if (iface.family === 'IPv4' && !iface.internal) {
          return iface.broadcast;
        }
      }
    }
    return '255.255.255.255';
  }

  setupDiscovery(callback) {
    this.discoveryListener = dgram.createSocket('udp4');
    this.discoveryListener.on('error', (err) => {
      console.error(`Discovery listener error: ${err.stack}`);
      this.discoveryListener.close();
    });
    this.discoveryListener.on('message', (msg, rinfo) => {
      this.discoveryReceive(msg, rinfo);
    });
    this.discoveryListener.bind(41234, () => {
      if (callback) callback();
    });
  }

  discoveryBroadcast(type, message, callback) {
    const client = dgram.createSocket('udp4');
    const data = JSON.stringify({ type, ...message });
    client.send(data, 0, data.length, 41234, this.broadcastIP, (err) => {
      if (err) console.error(err);
      client.close();
      this.lastDiscoveryBroadcast = Date.now();
      if (callback) callback();
    });
  }

  discoveryReceive(msg, rinfo) {
    try {
      const data = JSON.parse(msg);
      if (data.type === 'heartbeat') {
        const hostname = data.hostname;
        this.nearbyServers[hostname] = {
          ip: rinfo.address,
          port: rinfo.port,
          timestamp: Date.now()
        };
      }
    } catch (e) {
      console.error('Invalid message:', e);
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
      this.discoveryListener = null;
    }
  }
}
