const WebSocket = require('ws');

class Chrome extends EventEmitter {
  constructor(options, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}`;
    this._ws = null;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
  }

  async _start() {
    await this._connectToWebSocket();
  }

  async _connectToWebSocket() {
    this._ws = new WebSocket(this.webSocketUrl);
    this._ws.on('open', () => {
      this.emit('connect');
    });
    this._ws.on('message', (message) => {
      this._handleMessage(message);
    });
    this._ws.on('close', () => {
      this._handleConnectionClose();
    });
    this._ws.on('error', (err) => {
      this.emit('error', err);
    });
  }

  _handleConnectionClose() {
    if (this._ws) {
      this._ws.removeAllListeners();
      this._ws = null;
    }
    this._callbacks = {};
    this.emit('close');
  }

  _handleMessage(message) {
    try {
      const data = JSON.parse(message.toString());
      if (data.id) {
        const callback = this._callbacks[data.id];
        if (callback) {
          delete this._callbacks[data.id];
          callback(null, data.result);
        }
      } else if (data.method) {
        this._notifier.emit(data.method, data.params);
      } else if (data.error) {
        const callback = this._callbacks[data.id];
        if (callback) {
          delete this._callbacks[data.id];
          callback(data.error, null);
        }
      }
    } catch (err) {
      this.emit('error', err);
    }
  }

  send(method, params, sessionId, callback) {
    const id = this._nextCommandId++;
    const command = {
      id,
      method,
      params
    };
    if (sessionId) {
      command.sessionId = sessionId;
    }
    this._callbacks[id] = callback;
    this._ws.send(JSON.stringify(command));
  }

  _enqueueCommand(method, params, sessionId, callback) {
    this.send(method, params, sessionId, callback);
  }

  close(callback) {
    if (this._ws) {
      this._ws.close();
      this._ws = null;
      if (callback) {
        callback();
      }
    }
  }
}

module.exports = Chrome;
