    super();
    this.httpServer = http.createServer();
    this.wsServer = new WebSocket.Server({ server: this.httpServer });
    this.state = Processor.ST_CLOSED;
    this.wsServer.on('connection', (socket, request) => {
      this.add(socket, request);
    });
    this.wsServer.on('error', (err) => this.emit('error', err));
    this.wsServer.on('close', () => {
      this.state = Processor.ST_CLOSED;
      this.emit('close');
    });
  }

  add(socket, data) {
    socket.on('message', (message) => this.emit('message', message, socket));
    socket.on('close', () => this.emit('close', socket));
    socket.on('error', (err) => this.emit('error', err));
    if (data) socket.send(data);
  }

  close() {
    this.wsServer.close(() => {
      this.httpServer.close(() => {
        this.state = Processor.ST_CLOSED;
        this.emit('close');
      });
    });
  }
}

