const crypto = require('crypto');
const request = require('request');

class Cryptsy {
  constructor(key, secret) {
    this.key = key;
    this.secret = secret;
    this.PUBLIC_API_URL = 'http://pubapi.cryptsy.com/api.php';
    this.PRIVATE_API_URL = 'https://api.cryptsy.com/api';
    this.USER_AGENT = 'nomp/node-open-mining-portal';
    this.STRICT_SSL = true;
  }

  _getPrivateHeaders(parameters) {
    const nonce = Date.now();
    const data = `${nonce}${this.key}`;
    const hmac = crypto.createHmac('sha512', this.secret);
    hmac.update(data);
    const signature = hmac.digest('hex');
    return {
      'Key': this.key,
      'Sign': signature,
      'Content-Type': 'application/json',
      'User-Agent': this.USER_AGENT
    };
  }

  _request(options, callback) {
    const reqOptions = {
      url: options.url,
      method: options.method,
      headers: options.headers,
      json: true,
      strictSSL: this.STRICT_SSL
    };

    if (options.body) {
      reqOptions.body = options.body;
    }

    request(reqOptions, (error, response, body) => {
      if (error) {
        return callback(error);
      }
      if (response.statusCode !== 200) {
        return callback(new Error(`HTTP ${response.statusCode}: ${body}`));
      }
      callback(null, body);
    });
  }

  _public(parameters, callback) {
    const url = this.PUBLIC_API_URL;
    const params = new URLSearchParams(parameters).toString();
    const options = {
      url: `${url}?${params}`,
      method: 'GET',
      headers: {
        'User-Agent': this.USER_AGENT
      }
    };
    this._request(options, callback);
  }

  _private(parameters, callback) {
    const headers = this._getPrivateHeaders(parameters);
    const options = {
      url: this.PRIVATE_API_URL,
      method: 'POST',
      headers: headers,
      body: JSON.stringify(parameters)
    };
    this._request(options, callback);
  }

  getTicker(callback) {
    this._public({ command: 'getticker' }, callback);
  }

  getOrderBook(currencyA, currencyB, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._public({ command: 'getorderbook', market: market }, callback);
  }

  getTradeHistory(currencyA, currencyB, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._public({ command: 'gettrades', market: market }, callback);
  }

  myBalances(callback) {
    this._private({ command: 'getbalances' }, callback);
  }

  myOpenOrders(currencyA, currencyB, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._private({ command: 'getopenorders', market: market }, callback);
  }

  myTradeHistory(currencyA, currencyB, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._private({ command: 'getmytrades', market: market }, callback);
  }

  buy(currencyA, currencyB, rate, amount, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._private({
      command: 'buy',
      market: market,
      rate: rate,
      amount: amount
    }, callback);
  }

  sell(currencyA, currencyB, rate, amount, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._private({
      command: 'sell',
      market: market,
      rate: rate,
      amount: amount
    }, callback);
  }

  cancelOrder(currencyA, currencyB, orderNumber, callback) {
    const market = this._joinCurrencies(currencyA, currencyB);
    this._private({
      command: 'cancelorder',
      market: market,
      orderNumber: orderNumber
    }, callback);
  }

  withdraw(currency, amount, address, callback) {
    this._private({
      command: 'withdraw',
      currency: currency,
      amount: amount,
      address: address
    }, callback);
  }

  _joinCurrencies(currencyA, currencyB) {
    return `${currencyA}_${currencyB}`;
  }
}

