const WebSocket = require('ws');

class WebSocketImpl {
  constructor(globalObject, args, privateData) {
    this._globalObject = globalObject;
    this._ownerDocument = privateData.ownerDocument;
    this._urlRecord = { url: args.url };
    this.url = args.url;
    this._ws = null;
    this._readyState = 0;
    this._requiredToFail = false;
    this.bufferedAmount = 0;
    this._sendQueue = [];

    this._ws = new WebSocket(this.url);

    this._ws.on('open', () => this._onConnectionEstablished());
    this._ws.on('message', (data, isBinary) => this._onMessageReceived(data, isBinary));
    this._ws.on('close', (code, reason) => this._onConnectionClosed(code, reason));
    this._ws.on('error', (error) => {
      console.error('WebSocket error:', error);
    });

    this.bufferedAmount = this._ws.bufferedAmount;
  }

  _onConnectionEstablished() {
    this._readyState = 1;
    while (this._sendQueue.length > 0) {
      const data = this._sendQueue.shift();
      this._ws.send(data);
    }
  }

  _onMessageReceived(data, isBinary) {
    console.log('Received:', data.toString());
  }

  _onConnectionClosed(code, reason) {
    this._readyState = 3;
  }

  close(code, reason) {
    if (this._ws) {
      this._ws.close(code, reason);
    }
  }

  send(data) {
    if (this._readyState === 1) {
      this._ws.send(data);
    } else {
      this._sendQueue.push(data);
    }
  }

  get readyState() {
    return this._readyState;
  }
}

module.exports = WebSocketImpl;

