class ReconnectingWebSocket {
    constructor(url, protocols, options) {
        this.debug = false;
        this.automaticOpen = true;
        this.reconnectInterval = 1000;
        this.maxReconnectInterval = 30000;
        this.reconnectDecay = 1.5;
        this.timeoutInterval = 2000;

        if (options) {
            for (var key in options) {
                this[key] = options[key];
            }
        }

        this.url = url;
        this.reconnectAttempts = 0;
        this.readyState = WebSocket.CONNECTING;
        this.protocol = null;

        var forcedClose = false;
        var timedOut = false;
        var eventTarget = document.createElement('div');

        eventTarget.addEventListener('open', (event) => { this.onopen(event); });
        eventTarget.addEventListener('close', (event) => { this.onclose(event); });
        eventTarget.addEventListener('connecting', (event) => { this.onconnecting(event); });
        eventTarget.addEventListener('message', (event) => { this.onmessage(event); });
        eventTarget.addEventListener('error', (event) => { this.onerror(event); });

        this.addEventListener = eventTarget.addEventListener.bind(eventTarget);
        this.removeEventListener = eventTarget.removeEventListener.bind(eventTarget);
        this.dispatchEvent = eventTarget.dispatchEvent.bind(eventTarget);

        this.open = function(wasReconnect) {
            var ws = new WebSocket(this.url, protocols || []);
            if (!wasReconnect) {
                eventTarget.dispatchEvent(this.createEvent('connecting'));
            }

            var timeout = setTimeout(() => {
                timedOut = true;
                ws.close();
                timedOut = false;
            }, this.timeoutInterval);

            ws.onopen = () => {
                clearTimeout(timeout);
                this.protocol = ws.protocol;
                this.readyState = WebSocket.OPEN;
                this.reconnectAttempts = 0;
                var event = this.createEvent('open');
                event.isReconnect = wasReconnect;
                wasReconnect = false;
                eventTarget.dispatchEvent(event);
            };

            ws.onclose = (event) => {
                clearTimeout(timeout);
                ws = null;
                if (forcedClose) {
                    this.readyState = WebSocket.CLOSED;
                    eventTarget.dispatchEvent(this.createEvent('close'));
                } else {
                    this.readyState = WebSocket.CONNECTING;
                    var connectingEvent = this.createEvent('connecting');
                    connectingEvent.code = event.code;
                    connectingEvent.reason = event.reason;
                    connectingEvent.wasClean = event.wasClean;
                    eventTarget.dispatchEvent(connectingEvent);

                    if (!wasReconnect && !timedOut) {
                        eventTarget.dispatchEvent(this.createEvent('close'));
                    }

                    var reconnectTimeout = this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts);
                    setTimeout(() => {
                        this.reconnectAttempts++;
                        this.open(true);
                    }, Math.min(reconnectTimeout, this.maxReconnectInterval));
                }
            };

            ws.onmessage = (event) => {
                var messageEvent = this.createEvent('message');
                messageEvent.data = event.data;
                eventTarget.dispatchEvent(messageEvent);
            };

            ws.onerror = (event) => {
                eventTarget.dispatchEvent(this.createEvent('error'));
            };
        };

        if (this.automaticOpen) {
            this.open(false);
        }

        this.send = function(data) {
            if (ws) {
                return ws.send(data);
            } else {
                throw 'INVALID_STATE_ERR : Pausing to reconnect websocket';
            }
        };

        this.close = function(code, reason) {
            forcedClose = true;
            if (ws) {
                ws.close(code || 1000, reason);
            }
        };

        this.refresh = function() {
            if (ws) {
                ws.close();
            }
        };
    }

    createEvent(eventName, detail) {
        var event = document.createEvent('CustomEvent');
        event.initCustomEvent(eventName, false, false, detail);
        return event;
    }

    onopen(event) {}
    onclose(event) {}
    onconnecting(event) {}
    onmessage(event) {}
    onerror(event) {}
}

