    this.reconnectInterval = options.reconnectInterval ?? 1000;
    this.maxReconnectInterval = options.maxReconnectInterval ?? 30000;
    this.reconnectDecay = options.reconnectDecay ?? 2;
    this.timeoutInterval = options.timeoutInterval ?? 5000;
    this.url = url;
    this.protocol = protocols || null;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CLOSED;
    this._isClosing = false;
    this._reconnectTimeout = null;
    this._connectTimeout = null;
    this._socket = null;
    this._eventListeners = {};
    this.onopen = null;
    this.onclose = null;
    this.onconnecting = null;
    this.onmessage = null;
    this.onerror = null;

    if (this.automaticOpen) {
      this.open();
    }
  }

  open() {
    this.dispatchEvent(new CustomEvent('connecting', { detail: {} }));
    if (this.onconnecting) this.onconnecting();
    this._socket = new WebSocket(this.url, this.protocol);
    this.readyState = WebSocket.CONNECTING;

    this._socket.onopen = (event) => this.onopenHandler(event);
    this._socket.onmessage = (event) => this.onmessageHandler(event);
    this._socket.onerror = (event) => this.onerrorHandler(event);
    this._socket.onclose = (event) => this.oncloseHandler(event);

    this._connectTimeout = setTimeout(() => {
      if (this.readyState === WebSocket.CONNECTING) {
        this.close();
        this.reconnect();
      }
    }, this.timeoutInterval);
  }

  onopenHandler(event) {
    this.readyState = WebSocket.OPEN;
    this.reconnectAttempts = 0;
    this._isClosing = false;
    this.dispatchEvent(new CustomEvent('open', { detail: event }));
    if (this.onopen) this.onopen(event);
  }

  onmessageHandler(event) {
    this.dispatchEvent(new CustomEvent('message', { detail: event }));
    if (this.onmessage) this.onmessage(event);
  }

  onerrorHandler(event) {
    this.dispatchEvent(new CustomEvent('error', { detail: event }));
    if (this.onerror) this.onerror(event);
  }

  oncloseHandler(event) {
    if (this._isClosing) {
      this._isClosing = false;
      this.readyState = WebSocket.CLOSED;
      this.dispatchEvent(new CustomEvent('close', { detail: event }));
      if (this.onclose) this.onclose(event);
      return;
    }

    this.reconnect();
  }

  reconnect() {
    const nextInterval = Math.min(
      this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
      this.maxReconnectInterval
    );
    this.reconnectAttempts++;

    this._reconnectTimeout = setTimeout(() => {
      this.open();
    }, nextInterval);
  }

  close(code, reason) {
    this._isClosing = true;
    if (this._socket) {
      this._socket.close(code, reason);
      this._socket = null;
    }
    if (this._connectTimeout) {
      clearTimeout(this._connectTimeout);
      this._connectTimeout = null;
    }
    if (this._reconnectTimeout) {
      clearTimeout(this._reconnectTimeout);
      this._reconnectTimeout = null;
    }
    this.readyState = WebSocket.CLOSED;
    this.dispatchEvent(new CustomEvent('close', { detail: { code, reason } }));
    if (this.onclose) this.onclose({ code, reason });
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this._socket.send(data);
    } else {
      throw new Error('WebSocket is not open.');
    }
  }

  addEventListener(type, listener) {
    if (!this._eventListeners[type]) {
      this._eventListeners[type] = [];
    }
    this._eventListeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (this._eventListeners[type]) {
      this._eventListeners[type] = this._eventListeners[type].filter(l => l !== listener);
    }
  }

  dispatchEvent(event) {
    const type = event.type;
    if (this._eventListeners[type]) {
      this._eventListeners[type].forEach(listener => {
        listener(event);
      });
    }
  }

  createEvent(eventName, detail) {
    return new CustomEvent(eventName, { detail });
  }

  refresh() {
    this.close();
    this.open();
  }
}
